document.getElementById("qr-text").addEventListener("keydown", (e) => {
  "Enter" === e.key && generateQrCode();
}),
  (generateQrCode = () => {
    let e = document.getElementById("qr-text").value,
      t = document.getElementById("qr-code");
    (t.innerHTML = ""),
      "" === e.trim()
        ? alert("Please enter a text to generate QR code")
        : new QRCode(t, {
            text: e,
            width: 200,
            height: 200,
            colorDark: "#000000",
          });
  });
