const search = document.getElementById("search-input");
search.addEventListener("keydown", (e) => {
  "Enter" === e.key && searchImage();
});
const searchImage = async () => {
  let e = search.value,
    s = document.getElementById("resulted-images-list");
  if ("" === e.trim()) {
    alert("Please enter a search term");
    return;
  }
  let t = await fetch(
      "https://api.unsplash.com/search/photos?query=" + e + "&client_id="
    ),
    a = await t.json();
  a.results.forEach((e) => {
    let t = document.createElement("img");
    (t.src = e.urls.small),
      (t.alt = e.alt_description),
      t.classList.add("rounded-lg", "shadow-md", "w-full", "cursor-pointer"),
      s.appendChild(t);
  });
};
