async function searchImages() {
  let e = document.getElementById("search-input").value,
    t = document.getElementById("image-results");
  if (((t.innerHTML = ""), "" === e.trim())) {
    alert("Please enter a search term");
    return;
  }
  let a = await fetch(
      `https://api.unsplash.com/search/photos?query=${e}&client_id=xKLwAFHBeU-A37eHENdQYJUAAe7Z_7BOs54aWebF2j0`
    ),
    s = await a.json();
  s.results.forEach((e) => {
    let a = document.createElement("img");
    (a.src = e.urls.small),
      (a.alt = e.alt_description),
      a.classList.add("rounded-lg", "shadow-md", "w-full"),
      t.appendChild(a);
  });
}
